import { React, Component } from "react";
import {Button, Text, InputText, View, FlatList, AppRegistry} from "react-native";

AppRegistry.registerComponent('main', () => App)
//Square function component
function Square(props) {
return (
<Button style={styles.square} onPress={props.onClick} title={props.value} />
)
}

//Board class component
class Board extends React.Component {
renderSquare(i) {
return (
<Square 
value={this.props.squares[i]}
onPress={() => this.props.onClick(i)}
/>
);
}
//Renders the board
render() {
return (
<View>
<View style={styles.board-row}>
{this.renderSquare(0)}
{this.renderSquare(1)}
{this.renderSquare(2)}
</View>
<View style={styles.board-row}>
{this.renderSquare(3)}
{this.renderSquare(4)}
{this.renderSquare(5)}
</View>
<View style={styles.board-row}>
{this.renderSquare(6)}
{this.renderSquare(7)}
{this.renderSquare(8)}
</View>
</View>
)
}
}

//App class for game functionallity and rendering the base
export default class App extends React.Component {
constructor(props) {
super(props);
this.state = {
history: [
{
squares: Array(9).fill(null)
}
],
stepNumber: 0,
xIsNext: true
};
}

//Handles a click
handleClick(i) {
const history = this.state.history.slice(0, this.state.stepNumber + 1);
const current = history[history.length - 1];
const squares = current.squares.slice();
if (calculateWinner(squares) || squares[i]) {
return;
}
squares[i] = this.state.xIsNext ? "X" : "O";
this.setState({
history: history.concat([
{
squares: squares
}
]),
stepNumber: history.length,
xIsNext: !this.state.xIsNext
})
}
jumpTo(step) {
this.setState({
stepNumber: step,
xIsNext: (step % 2) === 0,
});	
}

render() {
const history = this.state.history;
const current = history[this.state.stepNumber]
const winner = calculateWinner(current.squares);

const moves = history.map((step, move) => {
const desc = move ?
"Go to move #" + move :
"Go to game start";


return (
<Button key={move} onPress={() => this.jumpTo(move)} title={desc} />
)
})

let status;
status = winner ? "Winner: " + winner : "Next player: " + (this.state.xIsNext ? "X" : "O")

return (
<View style={styles.game}>
<View style={styles.game-board}>
<Board
squares={current.squares}
onPress={i => this.handleClick(i)}
/>
</View>
<View style={styles.gameinfo}>
<View>{status}</View>
<FlatList
data={[
{key: "keys"}
]} />
</View>
</View>
);
}
}

const styles = StylesSheet.create({
container: {
font: [14, "Century Gothic", "Futura", "sans-serif"],	
margin: 20
},

FlatList: {
paddingLeft: 30,
},

status: {
marginBottom: 10,
},

square: {
backgroundColor: "white",
border: [1, "solid", "#999",],
fontSize: 24,
fontWeight: 24,
lineHeight: 34,
marginRight: -1,
marginTop: -1,
padding: 0,
textAlign: center,
width: 34,
},

game: {
display: flex,
flexDirection: row,
},

gameinfo: {
marginLeft: 20,
}
});
